<script>
	import axios from 'axios'
	import server from '../config/server'

	export default {
		name: "IndexPage",

		data: function () {
      return {
        mainList: [],

				isActiveTable: false,

				isActiveInfo: true,

				isShowMainList: false,

				timeoutInfo: 5000,

				timeoutTable: 20000
      }
		},

		computed: {
			computedMainList: function () {
        let list = []
        for (let i = 0; i < 20; i++) {
          let item = {}
          item.id = this.mainList[i] ? this.mainList[i].id : i + 0.1
					item.nickname = this.mainList[i] ? this.mainList[i].nickname : ''
					item.lastName = this.mainList[i] ? this.mainList[i].surname : ''
					item.firstName = this.mainList[i] ? this.mainList[i].name : ''
					item.rating = this.mainList[i] ? this.mainList[i].score : ''
          list.push(item)
        }
        return list
			},

      computedAdditionalList1: function () {
        let list = []
        for (let i = 20; i < 50; i++) {
          let item = {}
          item.id = this.mainList[i] ? this.mainList[i].id : i + 0.1
          item.nickname = this.mainList[i] ? this.mainList[i].nickname : ''
          item.lastName = this.mainList[i] ? this.mainList[i].surname : ''
          item.firstName = this.mainList[i] ? this.mainList[i].name : ''
          item.rating = this.mainList[i] ? this.mainList[i].score : ''
          list.push(item)
        }
        return list
      },

      computedAdditionalList2: function () {
        let list = []
        for (let i = 50; i < 80; i++) {
          let item = {}
          item.id = this.mainList[i] ? this.mainList[i].id : i + 0.1
          item.nickname = this.mainList[i] ? this.mainList[i].nickname : ''
          item.lastName = this.mainList[i] ? this.mainList[i].surname : ''
          item.firstName = this.mainList[i] ? this.mainList[i].name : ''
          item.rating = this.mainList[i] ? this.mainList[i].score : ''
          list.push(item)
        }
        return list
      }
		},

		methods: {
			loadList () {
        return new Promise((resolve, reject) => {
          axios({
            url: `${server.host}${server.endpoints.results}`,
            method: 'GET'
          })
            .then((response) => {
              this.mainList = response.data
							resolve(response)
            })
            .catch((err) => {
              reject(err)
            })
				})
			},

      toggleBlock () {
        this.loadList()
        if (this.isActiveTable) {
          this.isActiveTable = false
					setTimeout(() => {
            this.isActiveInfo = true
					}, 380)
          setTimeout(() => {
            this.isShowMainList = false
          }, 850)
				}
        if (this.isActiveInfo) {
          this.isActiveInfo = false
          setTimeout(() => {
            this.isActiveTable = true
          }, 380)
          setTimeout(() => {
            this.isShowMainList = true
          }, 850)
        }
			},

			recursiveTimeout () {
        if (this.isActiveInfo) {
          setTimeout(() => {
            this.toggleBlock()
            this.recursiveTimeout()
          }, this.timeoutInfo)
				} else {
          setTimeout(() => {
            this.toggleBlock()
            this.recursiveTimeout()
					}, this.timeoutTable)
				}
			}
		},

		mounted () {
      this.recursiveTimeout()
		}
	}
</script>

