		&__skew-wrapper {
			display: flex;
			width: 81.875%;
			height: 100%;
			padding: 50px;
			transform-origin: left bottom;
			transform: skew(-20deg);
			background-color: white;
			box-sizing: border-box;
			z-index: 100;

			&--column {
				flex-direction: column;
			}
		}

		&__main-block {
			width: 40.21739%;
			margin-right: 4.7554%;
		}

		&__additional-block {
			display: flex;
			width: 56.11413%;

			& div {
				width: 50%;
			}
		}
	}

	.main-list {
		margin: 0;
		padding: 0;
		list-style: none;

		&__item {
			display: flex;
			margin: 0 0 6px 0;
			padding: 6px 24px 6px 0;
			font-size: 2.7777vh;
			line-height: 3.0555vh;
			font-weight: bold;
			background-color: rgba(0,0,0,0.1);
		}

		&__rank {
			display: block;
			min-width: 50px;
			margin-right: 30px;
			text-align: right;
		}

		&__nick {
			display: block;
			flex-grow: 1;
			padding-right: 6px;
			text-overflow: ellipsis;
			white-space: nowrap;
			overflow: hidden;
		}

		&__points {
			display: block;
		}
	}

	.additional-list {
		display: flex;
		flex-direction: column;
		flex-wrap: wrap;
		max-height: 990px;
		margin: 5px 0 0 0;
		padding: 0;
		list-style: none;

		&__item {
			display: flex;
			flex-basis: 45%;
			margin: 0 6.4164% 5px 0;
			padding: 4px 24px 4px 0;
			font-size: 1.6666vh;
			line-height: 1.85188vh;
			background-color: rgba(0,0,0,0.1);
		}

		&__rank {
			display: block;
			min-width: 36px;
			margin-right: 30px;
			padding-right: 6px;
			text-align: right;
			text-overflow: ellipsis;
			white-space: nowrap;
			overflow: hidden;
		}

		&__nick {
			display: block;
			flex-grow: 1;
		}

		&__points {
			display: block;
		}
	}

	// animation effect
	.slide-effect {

		&-enter {
			transform: skew(0) translateX(2000px);

			&-active {
				transition: transform 0.3s;
				animation: skew-slide-enter  0.5s ease-in;
			}

			&-to {
				//transform: skew(-20deg) translateX(0);
			}
		}

		&-leave {
			transform: skew(-20deg) translateX(0);

			&-active {
				transition: transform 0.3s;
				animation: skew-slide-leave  0.5s ease-out;
			}

			&-to {
				//transform: skew(-20deg) translateX(-2000px);
			}
		}
	}
	
	@keyframes skew-slide-enter {
		from {
			transform: skew(-20deg) translateX(2000px);
		}
		50% {
			transform: skew(-20deg) translateX(0);
		}
		60% {
			transform: skew(-19deg) translateX(0);
		}
		80% {
			transform: skew(-19deg) translateX(0);
		}
		100% {
			transform: skew(-20deg) translateX(0);
		}
	}

	@keyframes skew-slide-leave {
		from {
			transform: skew(-20deg) translateX(0);
		}
		10% {
			transform: skew(-21deg) translateX(0);
		}
		50% {
			transform: skew(-20deg) translateX(0);
		}
		100% {
			transform: skew(-20deg) translateX(-2000px);
		}
	}

	.main-slide-fade-enter-active {
		@for $i from 1 to 21 {
			&:nth-child(#{$i}) {
				transition: all #{$i * 0.1}s ease;
			}
		}
	}

	.main-slide-fade-enter {
		transform: translateX(20px);
		opacity: 0;
	}

	.add-slide-fade-enter-active {
		@for $i from 1 to 3 {
			&:nth-child(#{$i}) {
				transition: all #{$i * 0.8}s ease 0.3s;
			}
		}
	}

	.add-slide-fade-enter {
		transform: translateX(10px);
		opacity: 0;
	}

	.info-slide {
		display: flex;
		flex-direction: column;
		justify-content: space-between;
		height: 100%;

		&__heading {
			font-size: 54px;
			text-align: center;
		}

		&__wrapper {

		}

		&__image-wrapper {
			display: flex;
			justify-content: center;
			margin: 80px 0;
			transform: skew(20deg);
		}

		&__card {
			width: 280px;
			height: 182px;
			margin: 0 66px;
			background-repeat: no-repeat;
			background-size: 100%;

			&--1 {
				background-image: url("../assets/image/group-2@3x.png");
			}

			&--2 {
				background-image: url("../assets/image/group-3@3x.png");
			}

			&--3 {
				background-image: url("../assets/image/group-4@3x.png");
			}
		}

		&__main-text-block {
			font-size: 30px;
			text-align: center;
		}

		&__foot-note {
			position: relative;

			& p {
				margin: 4px 0;
			}
		}
	}

	.qr-code {
		display: flex;
		align-items: center;
		padding: 20px;
		font-size: 30px;
		border: 1px solid rgba(0,0,0,0.1);
		border-radius: 4px;

		&__text-block {
			padding: 0 0 0 30px;
		}

		&__image {
			width: 180px;
			margin: 0 10px;
			padding: 10px 24px;

			& img {
				width: 100%;
				transform: skew(20deg);
			}
		}
	}
</style>